import {CachedElement} from '../index';

export default class HtmlEncoder {
  constructor() {
    this.cache = new CachedElement('div');
  }

  encode(text) {
    if (!text) {
      return text;
    }
    var div = this.cache.get();
    div.textContent = text;
    return div.innerHTML;
  }
}
